import React, { useState } from 'react';
import PlantCard from './PlantCard';
import PlantForm from './PlantForm';

const PlantsView = ({ plants, onAddPlant, onUpdatePlant, onDeletePlant }) => {
  const [showForm, setShowForm] = useState(false);
  const [currentPlant, setCurrentPlant] = useState(null);

  const handleAdd = () => {
    setCurrentPlant(null);
    setShowForm(true);
  };

  const handleEdit = (plant) => {
    setCurrentPlant(plant);
    setShowForm(true);
  };

  const handleSubmit = (plantData) => {
    if (currentPlant) {
      onUpdatePlant({ ...plantData, id: currentPlant.id });
    } else {
      onAddPlant(plantData);
    }
    setShowForm(false);
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-800">Inventario de Plantas</h2>
        <button
          onClick={handleAdd}
          className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700"
        >
          + Nueva Planta
        </button>
      </div>

      {showForm ? (
        <PlantForm
          initialData={currentPlant}
          onSubmit={handleSubmit}
          onCancel={() => setShowForm(false)}
        />
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {plants.map(plant => (
            <PlantCard
              key={plant.id}
              plant={plant}
              onEdit={handleEdit}
              onDelete={onDeletePlant}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default PlantsView;
